mapper.select:
select
        id as id,
        orderNo as orderNo,
        payNo as payNo,
        aliTradeNo as aliTradeNo,
        userId as userId,
        price as price,
        payType as payType,
        status as status,
        payChannel as payChannel,
        createdUserId as createdUserId,
        updatedUserId as updatedUserId,
        creatdTime as creatdTime,
        updatedTime as updatedTime,
        isDelete as isDelete
from pay_order


                and id=#{id}


                and orderNo=#{orderNo}


                and payNo=#{payNo}


                and aliTradeNo=#{aliTradeNo}


                and userId=#{userId}


                and price=#{price}


                and payType=#{payType}


                and status=#{status}


                and payChannel=#{payChannel}


                and createdUserId=#{createdUserId}


                and updatedUserId=#{updatedUserId}


                and creatdTime=#{creatdTime}


                and updatedTime=#{updatedTime}


                and isDelete=#{isDelete}



    limit #{beginPos},#{pageSize}

select count(*) from pay_order


            and id=#{id}


            and orderNo=#{orderNo}


            and payNo=#{payNo}


            and aliTradeNo=#{aliTradeNo}


            and userId=#{userId}


            and price=#{price}


            and payType=#{payType}


            and status=#{status}


            and payChannel=#{payChannel}


            and createdUserId=#{createdUserId}


            and updatedUserId=#{updatedUserId}


            and creatdTime=#{creatdTime}


            and updatedTime=#{updatedTime}


            and isDelete=#{isDelete}

mapper.delete:
delete from pay_order where id in (

            #{id}

