mapper.select:
select
        id as id,
        account as account,
        password as password,
        salt as salt,
        userName as userName,
        sex as sex,
        isAuthentication as isAuthentication,
        name as name,
        cardNo as cardNo,
        faceUrl as faceUrl,
        openId as openId,
        bindStatus as bindStatus,
        bindTime as bindTime,
        updatedUserId as updatedUserId,
        creatdTime as creatdTime,
        updatedTime as updatedTime,
        isDelete as isDelete
from users


                and id=#{id}


                and account=#{account}


                and password=#{password}


                and salt=#{salt}


                and userName=#{userName}


                and sex=#{sex}


                and isAuthentication=#{isAuthentication}


                and name=#{name}


                and cardNo=#{cardNo}


                and faceUrl=#{faceUrl}


                and openId=#{openId}


                and bindStatus=#{bindStatus}


                and bindTime=#{bindTime}


                and updatedUserId=#{updatedUserId}


                and creatdTime=#{creatdTime}


                and updatedTime=#{updatedTime}


                and isDelete=#{isDelete}



    limit #{beginPos},#{pageSize}

select count(*) from users


            and id=#{id}


            and account=#{account}


            and password=#{password}


            and salt=#{salt}


            and userName=#{userName}


            and sex=#{sex}


            and isAuthentication=#{isAuthentication}


            and name=#{name}


            and cardNo=#{cardNo}


            and faceUrl=#{faceUrl}


            and openId=#{openId}


            and bindStatus=#{bindStatus}


            and bindTime=#{bindTime}


            and updatedUserId=#{updatedUserId}


            and creatdTime=#{creatdTime}


            and updatedTime=#{updatedTime}


            and isDelete=#{isDelete}

